mapper.update:
update tb_article
set article_content=#{article_content}
, article_title=#{article_title}
, article_mod_dt=now()
, baseball = #{baseball}
, category=#{category}
, sex=#{sex}
where article_no = #{article_no}

update tb_article 
set hit_count = hit_count+1
where article_no = #{article_no}
